tables.table_schema:
CREATE TABLE property_appraisers (
    appraiser_id INT PRIMARY KEY,
    appraiser_name TEXT,
    join_date DATE
);

CREATE TABLE property_appraisers (
  appraiser_id INT PRIMARY KEY,
  appraiser_name VARCHAR(50) NOT NULL,
  appraiser_email VARCHAR(50) UNIQUE
);

CREATE TABLE appraisers (
    appraiser_id INT PRIMARY KEY,
    last_name VARCHAR(50) NOT NULL,
    first_name VARCHAR(50) NOT NULL
);

CREATE TABLE properties (
    property_id INT PRIMARY KEY,
    property_name VARCHAR(100) NOT NULL,
    property_type VARCHAR(50) NOT NULL,
    address VARCHAR(100) NOT NULL
);

CREATE TABLE valuations (
    valuation_id INT PRIMARY KEY,
    property_id INT NOT NULL,
    valuation_date DATE NOT NULL,
    valuation_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE appraisals (
    appraisal_id INT PRIMARY KEY,
    appraiser_id INT NOT NULL,
    property_id INT NOT NULL,
    valuation_id INT NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(50) NOT NULL
);
