tables.table_schema:
CREATE TABLE rental_properties (
    property_id UUID PRIMARY KEY,
    property_name VARCHAR,
    property_location VARCHAR,
    property_price INT
);

CREATE TABLE market_comparison (
    comparison_id UUID PRIMARY KEY,
    property_id UUID REFERENCES rental_properties(property_id),
    market_price INT,
    comparison_date DATE
);

CREATE TABLE market_comparison (
  comparison_id INT PRIMARY KEY,
  property_id INT REFERENCES property_availability(property_id),
  market_rental_price DECIMAL(10, 2),
  market_average_price DECIMAL(10, 2)
);

CREATE TABLE property_owners (
  owner_id INT PRIMARY KEY,
  owner_name VARCHAR(255),
  contact_email VARCHAR(255),
  contact_phone VARCHAR(20)
);

CREATE TABLE market_comparison (
  comparison_id INT PRIMARY KEY,
  property_type VARCHAR(255),
  rental_price DECIMAL,
  market_average_price DECIMAL
);

CREATE TABLE market_comparison (
    id INT PRIMARY KEY,
    property_id INT,
    market_rent_price DECIMAL(10, 2),
    market_comparison_date DATE
);

CREATE TABLE property_images (
    id INT PRIMARY KEY,
    property_id INT,
    image_url VARCHAR(255)
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    phone_number VARCHAR(15)
);
