tables.table_schema:
CREATE TABLE property_data (
    property_id UUID PRIMARY KEY,
    assessment_value DECIMAL,
    tax_records JSONB,
    ownership_history JSONB
);

CREATE TABLE property_assessments (
  id UUID PRIMARY KEY,
  property_id UUID NOT NULL,
  assessment_date DATE NOT NULL,
  assessed_value DECIMAL NOT NULL
);

CREATE TABLE property_assessments ( 
    id UUID PRIMARY KEY, 
    address VARCHAR(255), 
    assessment_value DECIMAL, 
    assessment_date DATE 
);

CREATE TABLE tax_records ( 
    id UUID PRIMARY KEY, 
    property_id UUID REFERENCES property_assessments(id), 
    tax_amount DECIMAL, 
    tax_date DATE 
);

CREATE TABLE property_assessments(
    property_id INT PRIMARY KEY,
    assessment_value DECIMAL,
    assessment_date DATE,
    assessed_by VARCHAR(50)
);

CREATE TABLE property_assessments (
  property_id INT PRIMARY KEY,
  address VARCHAR(255),
  assessment_value DECIMAL(10,2),
  assessment_date DATE
);
