tables.table_schema:
CREATE TABLE CompetitionAnalysis (
    neighborhood_name VARCHAR(100),
    number_of_listings INT
);

CREATE TABLE neighborhoods (
    neighborhood_id INT,
    neighborhood_name VARCHAR(255),
    listings_count INT
);

CREATE TABLE listings (
    listing_id INT,
    neighborhood_id INT,
    listing_price DECIMAL(10, 2),
    listing_type VARCHAR(50)
);

CREATE TABLE Neighborhoods (
  neighborhood_id INT PRIMARY KEY,
  neighborhood_name VARCHAR(100) NOT NULL
);

CREATE TABLE Listings (
  listing_id INT PRIMARY KEY,
  listing_name VARCHAR(100) NOT NULL,
  neighborhood_id INT NOT NULL,
  FOREIGN KEY (neighborhood_id) REFERENCES Neighborhoods(neighborhood_id)
);

CREATE TABLE Competitors (
  competitor_id INT PRIMARY KEY,
  competitor_name VARCHAR(100) NOT NULL
);

CREATE TABLE neighborhood_data (
    neighborhood_id INT PRIMARY KEY,
    neighborhood_name STRING
);

CREATE TABLE competitor_data (
    competitor_id INT PRIMARY KEY,
    competitor_name STRING,
    neighborhood_id INT
);

CREATE TABLE analysis_data (
    analysis_id INT PRIMARY KEY,
    neighborhood_id INT,
    total_listings INT,
    date_analyzed DATE
);
