tables.table_schema:
CREATE TABLE PropertyAffordability (
    property_id INT PRIMARY KEY,
    property_name VARCHAR(255),
    property_price DECIMAL(10, 2),
    owner_income DECIMAL(10, 2)
);

CREATE TABLE property_price (
  property_id INT PRIMARY KEY,
  price DECIMAL(12, 2),
  property_type VARCHAR(255),
  location VARCHAR(255)
);

CREATE TABLE income_level (
  income_id INT PRIMARY KEY,
  income DECIMAL(12, 2),
  occupation VARCHAR(255),
  location VARCHAR(255)
);

CREATE TABLE income_levels (
    income_id INT PRIMARY KEY,
    income_level DECIMAL(10, 2),
    income_range VARCHAR(50)
);

CREATE TABLE income_levels (
    income_id UUID PRIMARY KEY,
    income_level DECIMAL(10, 2),
    income_source VARCHAR(50)
);

CREATE TABLE property_owner (
    owner_id UUID PRIMARY KEY,
    property_id UUID,
    owner_name VARCHAR(100),
    owner_contact VARCHAR(15)
);

CREATE TABLE Properties (
  property_id INT PRIMARY KEY,
  address VARCHAR(255),
  price DECIMAL(10, 2),
  size_sqft INT
);
