tables.table_schema:
CREATE TABLE projects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(255),
    development_phase VARCHAR(50),
    estimated_cost DECIMAL(10,2)
);

CREATE TABLE zoning_regulations (
    zoning_id INT PRIMARY KEY,
    zoning_area VARCHAR(50),
    allowed_height DECIMAL(6,2),
    allowed_floor_area DECIMAL(10,2)
);
