tables.table_schema:
CREATE TABLE property_sales (
    property_id INT PRIMARY KEY,
    sale_date DATE,
    sale_price DECIMAL(12, 2),
    property_type VARCHAR(255)
);

CREATE TABLE property_trends (
    trend_id INT PRIMARY KEY,
    trend_date DATE,
    average_price DECIMAL(12, 2),
    trend_description VARCHAR(255)
);

CREATE TABLE PropertyTrends ( 
 trend_id INT PRIMARY KEY, 
 property_id INT, 
 trend_date DATE, 
 market_value DECIMAL(10, 2) 
 );

CREATE TABLE PropertyForecast ( 
 forecast_id INT PRIMARY KEY, 
 property_id INT, 
 forecast_date DATE, 
 forecast_value DECIMAL(10, 2) 
 );

CREATE TABLE MarketValues (
    id SERIAL PRIMARY KEY,
    property_id INTEGER,
    date DATE,
    value DECIMAL(10,2)
);

CREATE TABLE PropertyOwners (
    id SERIAL PRIMARY KEY,
    property_id INTEGER,
    owner_name VARCHAR(255),
    owner_email VARCHAR(255)
);

CREATE TABLE SalesHistory (
    id SERIAL PRIMARY KEY,
    property_id INTEGER,
    sale_date DATE,
    sale_price DECIMAL(10,2)
);

CREATE TABLE PropertyTrends (
    id SERIAL PRIMARY KEY,
    property_id INTEGER,
    date DATE,
    trend VARCHAR(255)
);
