  property_address VARCHAR(255),
  assessed_value DECIMAL(10,2),
  tax_rate DECIMAL(5,2)
);

CREATE TABLE property_tax_info (
    property_id INT PRIMARY KEY,
    property_address TEXT,
    assessment_value DECIMAL,
    tax_year INT
);

CREATE TABLE assessment_values (
    assessment_id INT PRIMARY KEY,
    property_id INT,
    assessment_date DATE,
    assessed_value DECIMAL
);

CREATE TABLE payment_records (
    payment_id INT PRIMARY KEY,
    property_id INT,
    payment_date DATE,
    amount_paid DECIMAL
);

CREATE TABLE PropertyTaxInformation (
  property_id UUID PRIMARY KEY,
  property_address TEXT NOT NULL,
  tax_year INT NOT NULL,
  tax_amount NUMERIC
);

CREATE TABLE AssessmentValues (
  assessment_id UUID PRIMARY KEY,
  property_id UUID NOT NULL,
  assessment_date DATE NOT NULL,
  assessed_value NUMERIC
);

CREATE TABLE PaymentRecords (
  payment_id UUID PRIMARY KEY,
  property_id UUID NOT NULL,
  payment_date DATE NOT NULL,
  payment_amount NUMERIC
);

CREATE TABLE TaxCompliance (
  compliance_id UUID PRIMARY KEY,
  property_id UUID NOT NULL,
  compliance_status BOOLEAN NOT NULL
);
