tables.table_schema:
CREATE TABLE property_valuation_reports (
  report_id UUID PRIMARY KEY,
  property_id UUID NOT NULL,
  valuation_amount DECIMAL NOT NULL,
  appraisal_date DATE NOT NULL
);

CREATE TABLE investment_analysis (
  analysis_id UUID PRIMARY KEY,
  property_id UUID NOT NULL,
  analysis_date DATE NOT NULL,
  investment_return DECIMAL NOT NULL
);

CREATE TABLE property_valuation_reports (
  report_id UUID PRIMARY KEY,
  report_date DATE,
  property_address VARCHAR(255),
  appraiser_name VARCHAR(100)
);

CREATE TABLE appraisals (
  appraisal_id UUID PRIMARY KEY,
  report_id UUID REFERENCES property_valuation_reports(report_id),
  appraisal_date DATE,
  appraisal_value DECIMAL(10,2)
);

CREATE TABLE investment_analysis (
  analysis_id UUID PRIMARY KEY,
  appraisal_id UUID REFERENCES appraisals(appraisal_id),
  analysis_date DATE,
  return_rate DECIMAL(5,2)
);

CREATE TABLE users (
  user_id UUID PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(255),
  role VARCHAR(20)
);
