tables.table_schema:
CREATE TABLE Vehicles (
    id INT PRIMARY KEY,
    make VARCHAR(255) NOT NULL,
    model VARCHAR(255) NOT NULL,
    registration_number VARCHAR(50) NOT NULL
);

CREATE TABLE vehicles (
    vehicle_id UUID PRIMARY KEY,
    make VARCHAR(255),
    model VARCHAR(255),
    registration_number VARCHAR(50)
);

CREATE TABLE drivers (
    driver_id UUID PRIMARY KEY,
    name VARCHAR(255),
    age INT,
    contact_number VARCHAR(15)
);

CREATE TABLE vehicle_make (
    make_id INT PRIMARY KEY,
    make_name VARCHAR(255) NOT NULL
);

CREATE TABLE vehicle_model (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(255) NOT NULL,
    make_id INT NOT NULL,
    FOREIGN KEY (make_id) REFERENCES vehicle_make(make_id)
);

CREATE TABLE vehicle_registration (
    reg_id INT PRIMARY KEY,
    reg_number VARCHAR(20) NOT NULL
);

CREATE TABLE VehicleMake (
    make_id UUID PRIMARY KEY,
    make_name STRING
);

CREATE TABLE VehicleModel (
    model_id UUID PRIMARY KEY,
    model_name STRING,
    make_id UUID REFERENCES VehicleMake(make_id)
);

CREATE TABLE VehicleRegistration (
    reg_id UUID PRIMARY KEY,
    reg_number STRING UNIQUE,
    vehicle_id UUID REFERENCES VehicleModel(model_id)
);

CREATE TABLE VehicleInsurance (
    insurance_id UUID PRIMARY KEY,
    insurance_provider STRING,
    reg_id UUID REFERENCES VehicleRegistration(reg_id)
);

CREATE TABLE VehicleLocation (
    location_id UUID PRIMARY KEY,
    location_name STRING,
    reg_id UUID REFERENCES VehicleRegistration(reg_id)
);
