tables.table_schema:
CREATE TABLE fuel_consumption (
    vehicle_id INT,
    date DATE,
    fuel_amount DECIMAL,
    cost DECIMAL
);

CREATE TABLE maintenance_costs (
    vehicle_id INT,
    date DATE,
    repair_cost DECIMAL,
    maintenance_type VARCHAR(100)
);

CREATE TABLE BudgetPlanning (
  year INT,
  planned_budget DECIMAL(10,2),
  actual_expenses DECIMAL(10,2),
  PRIMARY KEY (year)
);

CREATE TABLE budget_planning (
    id INT PRIMARY KEY,
    year INT,
    budget_amount FLOAT,
    actual_expense FLOAT
);

CREATE TABLE FuelConsumption (
    vehicle_id INT,
    date DATE,
    liters_consumed FLOAT,
    cost_per_liter DECIMAL(10,2)
);

CREATE TABLE MaintenanceServices (
    service_id INT,
    service_type VARCHAR(50),
    description TEXT
);
