tables.table_schema:
CREATE TABLE RouteEfficiency (
    route_id INT PRIMARY KEY,
    route_name VARCHAR(255),
    distance FLOAT,
    avg_speed FLOAT
);

CREATE TABLE TrafficPatterns (
    pattern_id INT PRIMARY KEY,
    pattern_name VARCHAR(255),
    congestion_rate FLOAT,
    road_conditions VARCHAR(255)
);

CREATE TABLE vehicles (
  vehicle_id INT PRIMARY KEY,
  vehicle_type TEXT,
  max_load_kg INT,
  current_location TEXT
);

CREATE TABLE traffic_patterns (
  pattern_id INT PRIMARY KEY,
  congestion_level TEXT,
  average_speed_kmph FLOAT,
  timestamp TIMESTAMP
);
