tables.table_schema:
CREATE TABLE compliance_data (
    vehicle_id INT PRIMARY KEY,
    last_inspection_date DATE,
    next_inspection_date DATE,
    passed_inspection BOOLEAN
);

CREATE TABLE regulatory_compliance (
    vehicle_id INT PRIMARY KEY,
    inspection_date DATE,
    inspector_name VARCHAR(50),
    compliance_status BOOLEAN
);

CREATE TABLE safety_inspections (
    inspection_id INT PRIMARY KEY,
    vehicle_id INT,
    inspection_date DATE,
    safety_report TEXT
);

CREATE TABLE transportation_vehicles (
    vehicle_id INT PRIMARY KEY,
    vehicle_type VARCHAR(50),
    manufacturer VARCHAR(50),
    production_year INT
);

CREATE TABLE regulations (
  regulation_id INT PRIMARY KEY,
  regulation_name VARCHAR(100),
  description TEXT
);

CREATE TABLE regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(100),
    description TEXT
);

CREATE TABLE inspection_details (
    id INT PRIMARY KEY,
    inspection_id INT,
    issue_description TEXT,
    resolution_status VARCHAR(20)
);

CREATE TABLE violations (
    violation_id INT PRIMARY KEY,
    vehicle_id INT,
    violation_description TEXT,
    fine_amount DECIMAL(10, 2)
);
