tables.table_schema:
CREATE TABLE dbo.Inventory (
  id INT PRIMARY KEY,
  product_name VARCHAR(255),
  quantity INT,
  turnover_rate FLOAT
);

CREATE TABLE dbo.Orders (
  order_id INT PRIMARY KEY,
  customer_id INT,
  order_date DATE,
  fulfillment_rate FLOAT
);

CREATE TABLE inventory ( 
    product_id INT PRIMARY KEY, 
    product_name STRING, 
    quantity INT, 
    unit_price DECIMAL 
);

CREATE TABLE orders ( 
    order_id INT PRIMARY KEY, 
    customer_id INT, 
    order_date DATE, 
    total_amount DECIMAL 
);

CREATE TABLE kpis ( 
    kpi_id INT PRIMARY KEY, 
    kpi_name STRING, 
    kpi_value DECIMAL, 
    kpi_date DATE 
);

CREATE TABLE inventory_metrics (
    id SERIAL PRIMARY KEY,
    product_id INT,
    inventory_count INT,
    inventory_turnover DECIMAL
);

CREATE TABLE fulfillment_metrics (
    id SERIAL PRIMARY KEY,
    order_id INT,
    fulfillment_date DATE,
    fulfillment_status VARCHAR(50)
);

CREATE TABLE kpi_metrics (
    id SERIAL PRIMARY KEY,
    metric_id INT,
    metric_name VARCHAR(50),
    metric_value DECIMAL
);
