tables.table_schema:
CREATE TABLE barcode_scanners (
    id SERIAL PRIMARY KEY,
    scanner_model VARCHAR(50) NOT NULL,
    scanner_type VARCHAR(20) NOT NULL,
    manufacturer VARCHAR(50) NOT NULL
);

CREATE TABLE rfid_technology (
    id SERIAL PRIMARY KEY,
    rfid_model VARCHAR(50) NOT NULL,
    rfid_type VARCHAR(20) NOT NULL,
    frequency VARCHAR(20) NOT NULL
);

CREATE TABLE inventory_tracking (
    id SERIAL PRIMARY KEY,
    product_name VARCHAR(100) NOT NULL,
    category VARCHAR(50) NOT NULL,
    quantity INT NOT NULL
);
