tables.table_schema:
CREATE TABLE delivery_routes (
    id INT PRIMARY KEY,
    driver_id INT,
    route VARCHAR,
    distance FLOAT
);

CREATE TABLE delivery_data (
    delivery_id UUID PRIMARY KEY,
    driver_id UUID,
    delivery_time TIMESTAMP,
    delivery_address VARCHAR(255)
);

CREATE TABLE driver_routes (
    route_id UUID PRIMARY KEY,
    driver_id UUID,
    route_details JSONB
);

CREATE TABLE driver_performance (
    driver_id UUID PRIMARY KEY,
    total_deliveries INT,
    average_delivery_time INTERVAL
);

CREATE TABLE drivers (
 driver_id INT PRIMARY KEY,
 driver_name VARCHAR(50) NOT NULL,
 driver_phone VARCHAR(15) NOT NULL
);

CREATE TABLE deliveries (
 delivery_id INT PRIMARY KEY,
 driver_id INT NOT NULL,
 delivery_time TIMESTAMP NOT NULL,
 delivery_address VARCHAR(100) NOT NULL
);

CREATE TABLE delivery_history (
 delivery_id INT,
 driver_id INT,
 delivery_status VARCHAR(20) NOT NULL,
 delivery_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE routes (
 route_id INT PRIMARY KEY,
 driver_id INT NOT NULL,
 route_details TEXT
);

CREATE TABLE drivers (
  driver_id UUID PRIMARY KEY,
  driver_name VARCHAR(255),
  driver_rating FLOAT
);

CREATE TABLE delivery_orders (
  order_id UUID PRIMARY KEY,
  driver_id UUID REFERENCES drivers(driver_id),
  order_timestamp TIMESTAMP,
  order_address VARCHAR(255)
);

CREATE TABLE delivery_history (
  delivery_id UUID PRIMARY KEY,
  order_id UUID REFERENCES delivery_orders(order_id),
  route_id UUID REFERENCES delivery_routes(route_id),
  delivery_status VARCHAR(50)
);

CREATE TABLE driver_performance (
  driver_id UUID PRIMARY KEY REFERENCES drivers(driver_id),
  completed_deliveries INT,
  total_distance_covered FLOAT,
  average_rating FLOAT
);
