tables.table_schema:
CREATE TABLE inventory_level (
    id INT,
    product_name VARCHAR(255),
    quantity INT,
    delivery_hub_id INT
);

CREATE TABLE delivery_hub (
    id INT,
    hub_name VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE stock_availability (
    id INT,
    product_id INT,
    hub_id INT,
    quantity_available INT
);

CREATE TABLE delivery_hub (
  hub_id INT PRIMARY KEY,
  hub_location VARCHAR(255) NOT NULL
);

CREATE TABLE product (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(255) NOT NULL,
  category VARCHAR(50) NOT NULL,
  price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE delivery_hubs (
	hub_id SERIAL PRIMARY KEY,
	hub_name VARCHAR(50) NOT NULL,
	location VARCHAR(100) NOT NULL
);

CREATE TABLE inventory_items (
	item_id SERIAL PRIMARY KEY,
	item_name VARCHAR(50) NOT NULL,
	item_description TEXT,
	price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE stock_levels (
	stock_id SERIAL PRIMARY KEY,
	item_id INT REFERENCES inventory_items(item_id),
	hub_id INT REFERENCES delivery_hubs(hub_id),
	quantity INT NOT NULL
);

CREATE TABLE deliveries (
	delivery_id SERIAL PRIMARY KEY,
	delivery_date DATE NOT NULL,
	hub_id INT REFERENCES delivery_hubs(hub_id),
	status VARCHAR(20) NOT NULL
);

CREATE TABLE delivery_items (
	delivery_item_id SERIAL PRIMARY KEY,
	delivery_id INT REFERENCES deliveries(delivery_id),
	item_id INT REFERENCES inventory_items(item_id),
	quantity INT NOT NULL
);
