tables.table_schema:
CREATE TABLE driver_performance_metrics (
  driver_id INT,
  driving_speed INT,
  fuel_efficiency FLOAT
);

CREATE TABLE speed_metrics (
    driver_id INT,
    timestamp TIMESTAMP,
    speed FLOAT
);

CREATE TABLE fuel_metrics (
    driver_id INT,
    timestamp TIMESTAMP,
    fuel_efficiency FLOAT
);

CREATE TABLE Drivers (
    driver_id INT PRIMARY KEY,
    driver_name VARCHAR(50) NOT NULL,
    driving_speed INT,
    fuel_efficiency DECIMAL
);

CREATE TABLE PerformanceMetrics (
    trip_id INT,
    driving_speed INT,
    fuel_efficiency DECIMAL,
    penalty_points INT
);

CREATE TABLE drivers (
  driver_id SERIAL PRIMARY KEY,
  driver_name VARCHAR(50) NOT NULL,
  driving_speed INT NOT NULL,
  fuel_efficiency INT NOT NULL
);

CREATE TABLE trips (
  trip_id SERIAL PRIMARY KEY,
  driver_id INT,
  trip_date DATE NOT NULL,
  distance_traveled INT NOT NULL
);

CREATE TABLE speeding_events (
  event_id SERIAL PRIMARY KEY,
  driver_id INT,
  event_time TIMESTAMP NOT NULL,
  speed INT NOT NULL
);

CREATE TABLE fuel_consumption (
  consumption_id SERIAL PRIMARY KEY,
  driver_id INT,
  date DATE NOT NULL,
  gallons_consumed INT NOT NULL
);
