tables.table_schema:
CREATE TABLE cross_docking_facility_layouts (
    facility_id INT PRIMARY KEY,
    warehouse_name VARCHAR(50),
    storage_capacity INT,
    layout_description TEXT
);

CREATE TABLE cross_docking_facility (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(255) NOT NULL,
    facility_location VARCHAR(255) NOT NULL
);

CREATE TABLE cross_dock_facility (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL,
    capacity INT
);

CREATE TABLE storage_areas (
    area_id INT PRIMARY KEY,
    facility_id INT NOT NULL,
    area_name VARCHAR(50) NOT NULL,
    area_capacity INT
);

CREATE TABLE items (
    item_id INT PRIMARY KEY,
    area_id INT NOT NULL,
    item_name VARCHAR(50) NOT NULL,
    quantity INT
);

CREATE TABLE cross_docking_facility_layouts (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(100) NOT NULL,
    location VARCHAR(100) NOT NULL,
    capacity INT
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(100) NOT NULL,
    category VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE cross_docking_facility (
  facility_id INT PRIMARY KEY,
  facility_name VARCHAR(255) NOT NULL,
  total_capacity INT NOT NULL
);

CREATE TABLE product (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(255) NOT NULL,
  unit_price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE storage_area (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(255) NOT NULL,
  area_capacity INT NOT NULL
);

CREATE TABLE dock (
  dock_id INT PRIMARY KEY,
  dock_name VARCHAR(255) NOT NULL,
  dock_status VARCHAR(50) NOT NULL
);

CREATE TABLE dock_schedule (
  schedule_id INT PRIMARY KEY,
  dock_id INT NOT NULL,
  schedule_date DATE NOT NULL,
  schedule_time TIME NOT NULL
);
