CREATE TABLE Goods (
    goods_id UUID PRIMARY KEY,
    goods_name VARCHAR(255),
    goods_category VARCHAR(255),
    RFID_tag VARCHAR(50)
);

CREATE TABLE IncomingGoods (
    incoming_id UUID PRIMARY KEY,
    goods_id UUID REFERENCES Goods(goods_id),
    received_date TIMESTAMP,
    quantity INT
);

CREATE TABLE OutgoingGoods (
    outgoing_id UUID PRIMARY KEY,
    goods_id UUID REFERENCES Goods(goods_id),
    dispatched_date TIMESTAMP,
    destination VARCHAR(255)
);

CREATE TABLE employee (
    id UUID PRIMARY KEY,
    name TEXT NOT NULL,
    role TEXT NOT NULL
);
