tables.table_schema:
CREATE TABLE trends_analysis (
    analysis_id SERIAL PRIMARY KEY,
    analysis_date DATE,
    trend_type VARCHAR(50),
    analysis_result TEXT
);

CREATE TABLE cross_docking_efficiency (
    efficiency_id SERIAL PRIMARY KEY,
    cross_docking_date DATE,
    process_time INT,
    errors INT
);

CREATE TABLE warehouse_inventory (
    inventory_id SERIAL PRIMARY KEY,
    product_id INT,
    quantity INT,
    location VARCHAR(50)
);
