tables.table_schema:
CREATE TABLE production_schedule (
    id INT PRIMARY KEY,
    product_name VARCHAR(50),
    production_date DATE,
    estimated_quantity INT
);

CREATE TABLE manufacturing_progress (
    id INT PRIMARY KEY,
    product_id INT,
    progress_status VARCHAR(50),
    completion_date TIMESTAMP
);

CREATE TABLE Workers (
  worker_id INT PRIMARY KEY,
  worker_name VARCHAR(255) NOT NULL,
  department VARCHAR(255)
);

CREATE TABLE ManufacturingProcesses (
    id INT PRIMARY KEY,
    process_name VARCHAR(255),
    description TEXT,
    status VARCHAR(50)
);

CREATE TABLE EmployeeAssignments (
    id INT PRIMARY KEY,
    employee_id INT,
    process_id INT,
    assignment_date DATE
);

CREATE TABLE ProgressTracking (
    id INT PRIMARY KEY,
    process_id INT,
    progress_percentage DECIMAL(5,2),
    last_updated TIMESTAMP
);

CREATE TABLE progress_tracking (
    tracking_id SERIAL PRIMARY KEY,
    task_id INT REFERENCES production_tasks (task_id),
    process_id INT REFERENCES manufacturing_processes (process_id),
    progress_status VARCHAR(50) NOT NULL
);
