tables.table_schema:
CREATE TABLE returned_items (
    item_id INT PRIMARY KEY,
    item_name VARCHAR(255),
    status VARCHAR(50)
);

CREATE TABLE logistics_stages (
    stage_id INT PRIMARY KEY,
    stage_name VARCHAR(100),
    duration_minutes INT
);

CREATE TABLE returned_items (
    item_id INT PRIMARY KEY,
    return_date DATE,
    returned_by VARCHAR(50),
    status ENUM('received', 'processed', 'completed')
);

CREATE TABLE ReturnedItems (
  id INT PRIMARY KEY,
  item_name VARCHAR,
  return_date DATE,
  return_reason TEXT
);

CREATE TABLE Repair (
  id INT PRIMARY KEY,
  repair_date DATE,
  technician VARCHAR,
  repair_description TEXT
);

CREATE TABLE Reshipment (
  id INT PRIMARY KEY,
  reshipment_date DATE,
  destination_address VARCHAR,
  shipping_method VARCHAR
);

CREATE TABLE returned_items ( 
    item_id UUID PRIMARY KEY, 
    return_status VARCHAR(50), 
    return_timestamp TIMESTAMPTZ 
);

CREATE TABLE return_processing ( 
    return_id UUID PRIMARY KEY, 
    item_id UUID REFERENCES returned_items(item_id), 
    processing_stage VARCHAR(50), 
    processing_timestamp TIMESTAMPTZ 
);

CREATE TABLE return_inspection ( 
    inspection_id UUID PRIMARY KEY, 
    return_id UUID REFERENCES return_processing(return_id), 
    inspection_date DATE, 
    inspected_by VARCHAR(100) 
);

CREATE TABLE return_resolution ( 
    resolution_id UUID PRIMARY KEY, 
    return_id UUID REFERENCES return_processing(return_id), 
    resolution_status VARCHAR(50), 
    resolution_timestamp TIMESTAMPTZ 
);
