tables.table_schema:
CREATE TABLE products (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(255) NOT NULL,
  inventory_count INT CHECK (inventory_count >= 0)
);

CREATE TABLE inventory (
    id INT PRIMARY KEY,
    product_name VARCHAR(50) NOT NULL,
    quantity INT NOT NULL CHECK (quantity >= 0),
    CONSTRAINT min_inventory_threshold CHECK (quantity >= 10)
);

CREATE TABLE orders (
    order_id INT PRIMARY KEY,
    product_id INT REFERENCES inventory(id),
    order_quantity INT NOT NULL,
    CONSTRAINT valid_order_quantity CHECK (order_quantity >= 1)
);

CREATE TABLE products (id INT PRIMARY KEY, name VARCHAR(255) NOT NULL, quantity INT NOT NULL CHECK (quantity >= 0));

CREATE TABLE suppliers (id INT PRIMARY KEY, name VARCHAR(255) NOT NULL);

CREATE TABLE inventory (product_id INT REFERENCES products (id), supplier_id INT REFERENCES suppliers (id), quantity INT NOT NULL CHECK (quantity >= 10));

CREATE TABLE sales (id INT PRIMARY KEY, product_id INT REFERENCES products (id), quantity INT NOT NULL CHECK (quantity > 0));

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255) NOT NULL,
    inventory_count INT CHECK (inventory_count >= 10)
);

CREATE TABLE manufacturers (
    manufacturer_id INT PRIMARY KEY,
    manufacturer_name VARCHAR(255) NOT NULL,
    country VARCHAR(50) NOT NULL
);

CREATE TABLE orders (
    order_id INT PRIMARY KEY,
    product_id INT REFERENCES products(product_id),
    quantity INT NOT NULL CHECK (quantity > 0),
    order_date TIMESTAMP NOT NULL
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(255) NOT NULL,
    email VARCHAR(255) UNIQUE
);

CREATE TABLE shipments (
    shipment_id INT PRIMARY KEY,
    order_id INT REFERENCES orders(order_id),
    customer_id INT REFERENCES customers(customer_id),
    shipment_date TIMESTAMP NOT NULL
);
