tables.table_schema:
CREATE TABLE Inventory (
    id INT PRIMARY KEY,
    product_name VARCHAR(50) INDEX,
    quantity INT INDEX,
    price DECIMAL(10, 2)
);

CREATE TABLE Inventory ( 
    id UUID PRIMARY KEY, 
    product_id UUID, 
    quantity INT, 
    location VARCHAR 
);

CREATE TABLE Product ( 
    id UUID PRIMARY KEY, 
    name VARCHAR, 
    category VARCHAR, 
    price DECIMAL 
);

CREATE TABLE Location ( 
    id UUID PRIMARY KEY, 
    name VARCHAR, 
    address VARCHAR 
);

CREATE TABLE Category ( 
    id INT PRIMARY KEY, 
    category_name VARCHAR(255) 
);

CREATE TABLE Supplier ( 
    id INT PRIMARY KEY, 
    supplier_name VARCHAR(255), 
    contact_name VARCHAR(255), 
    phone_number VARCHAR(15) 
);

CREATE TABLE Location ( 
    id INT PRIMARY KEY, 
    location_name VARCHAR(255), 
    address VARCHAR(255) 
);
