tables.table_schema:
CREATE TABLE historical_data (
    date TIMESTAMP,
    demand INT
);

CREATE TABLE predictions (
    date TIMESTAMP,
    predicted_demand INT
);

CREATE TABLE demand_predictions (
    id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    demand_value INT
);

CREATE TABLE historical_data (
    id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    value INT
);

CREATE TABLE trend_analysis (
    id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    trend_value INT
);

CREATE TABLE future_demand (
    id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    demand_prediction INT
);

CREATE TABLE historical_sales (
    sale_id UUID PRIMARY KEY,
    sale_date TIMESTAMP,
    product_id UUID,
    quantity INT,
    amount DECIMAL
);

CREATE TABLE product_details (
    product_id UUID PRIMARY KEY,
    product_name VARCHAR,
    category VARCHAR,
    manufacturer VARCHAR
);

CREATE TABLE demand_forecast (
    forecast_id UUID PRIMARY KEY,
    forecast_date TIMESTAMP,
    product_id UUID,
    forecast_value DECIMAL
);

CREATE TABLE historical_weather (
    weather_id UUID PRIMARY KEY,
    date TIMESTAMP,
    temperature DECIMAL,
    precipitation DECIMAL,
    wind_speed DECIMAL
);

CREATE TABLE location_details (
    location_id UUID PRIMARY KEY,
    location_name VARCHAR,
    city VARCHAR,
    country VARCHAR
);
