tables.table_schema:
CREATE TABLE demand_forecast (
    product_id INT PRIMARY KEY,
    date DATE,
    demand INT
);

CREATE TABLE factors (
    factor_id INT PRIMARY KEY,
    product_id INT,
    factor_name VARCHAR(50),
    factor_value VARCHAR(100)
);

CREATE TABLE DemandForecastFactors (
  factor_id UUID PRIMARY KEY,
  factor_name STRING,
  factor_weight FLOAT
);

CREATE TABLE DemandForecastData (
  data_id UUID PRIMARY KEY,
  factor_id UUID REFERENCES DemandForecastFactors(factor_id),
  demand_value FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE MachineLearningModels (
  model_id UUID PRIMARY KEY,
  model_name STRING,
  model_description STRING,
  training_data_table STRING REFERENCES DemandForecastData(data_id)
);

CREATE TABLE DemandFactors (
    id INT PRIMARY KEY,
    factor_name VARCHAR(50),
    factor_description TEXT
);

CREATE TABLE DemandForecast (
    forecast_id INT PRIMARY KEY,
    Date DATE,
    forecast_value DECIMAL,
    model_used VARCHAR(50)
);

CREATE TABLE ModelParameters (
    parameter_id INT PRIMARY KEY,
    parameter_name VARCHAR(50),
    parameter_value DECIMAL
);

CREATE TABLE PredictionResults (
    result_id INT PRIMARY KEY,
    Date DATE,
    predicted_value DECIMAL,
    actual_value DECIMAL
);

CREATE TABLE factors (
    factor_id INT PRIMARY KEY,
    factor_name VARCHAR(50),
    factor_type VARCHAR(50),
    created_at TIMESTAMP
);
