tables.table_schema:
CREATE TABLE seasonal_trends (
    id UUID PRIMARY KEY,
    date DATE,
    sales_amount DECIMAL,
    seasonal_factor DECIMAL
);
CREATE TABLE demand_predictions (
    id UUID PRIMARY KEY,
    date DATE,
    predicted_demand DECIMAL
);

CREATE TABLE seasonal_trends (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    season VARCHAR(50),
    demand FLOAT
);

CREATE TABLE sales_data (
    sale_id INT PRIMARY KEY,
    product_id INT REFERENCES seasonal_trends(product_id),
    sale_date DATE,
    sale_quantity INT
);

CREATE TABLE trend_analysis (
    analysis_id INT PRIMARY KEY,
    product_id INT REFERENCES seasonal_trends(product_id),
    trend_type VARCHAR(50),
    analysis_date DATE
);

CREATE TABLE demand_prediction (
    prediction_id INT PRIMARY KEY,
    product_id INT REFERENCES seasonal_trends(product_id),
    prediction_date DATE,
    predicted_demand FLOAT
);

CREATE TABLE seasonal_products(
  product_id INT PRIMARY KEY,
  product_name VARCHAR(50),
  price DECIMAL(10,2),
  season VARCHAR(20)
);

CREATE TABLE customer_data(
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(50),
  address VARCHAR(100),
  phone_number VARCHAR(15)
);

CREATE TABLE predictions(
  prediction_id INT PRIMARY KEY,
  product_id INT,
  prediction_date DATE,
  demand_estimate INT
);

CREATE TABLE seasonal_trends(
  trend_id INT PRIMARY KEY,
  season VARCHAR(20),
  trend_description VARCHAR(200)
);
