    id SERIAL PRIMARY KEY,
    product_name VARCHAR (100) NOT NULL,
    forecasted_demand INTEGER NOT NULL,
    forecast_date DATE NOT NULL
);

CREATE TABLE production_scheduling (
    id SERIAL PRIMARY KEY,
    product_name VARCHAR (100) NOT NULL,
    production_line VARCHAR (50) NOT NULL,
    schedule_date DATE NOT NULL
);

CREATE TABLE resource_allocation (
    id SERIAL PRIMARY KEY,
    resource_name VARCHAR (100) NOT NULL,
    allocated_quantity INTEGER NOT NULL,
    allocation_date DATE NOT NULL
);

CREATE TABLE integration_table (
    id SERIAL PRIMARY KEY,
    demand_forecasting_id INTEGER REFERENCES demand_forecasting(id),
    production_scheduling_id INTEGER REFERENCES production_scheduling(id),
    resource_allocation_id INTEGER REFERENCES resource_allocation(id)
);

CREATE TABLE demand_forecasting_data (
    id INT PRIMARY KEY,
    product_name VARCHAR(255),
    forecasted_demand INT,
    forecasted_date DATE
);

CREATE TABLE production_scheduling (
    id INT PRIMARY KEY,
    product_name VARCHAR(255),
    production_date DATE,
    production_quantity INT
);

CREATE TABLE resource_allocation (
    id INT PRIMARY KEY,
    resource_type VARCHAR(255),
    resource_quantity INT,
    allocated_date DATE
);

CREATE TABLE optimization_results (
    id INT PRIMARY KEY,
    optimization_date DATE,
    optimized_resource VARCHAR(255),
    allocation_quantity INT
);

CREATE TABLE integration_logs (
    id INT PRIMARY KEY,
    log_date DATE,
    log_message TEXT
);
