tables.table_schema:
CREATE TABLE robotic_arms_data (
  id SERIAL PRIMARY KEY,
  arm_id INT NOT NULL,
 timestamp TIMESTAMP NOT NULL,
 sensor_reading FLOAT NOT NULL
);

CREATE TABLE SensorData (
    id INT PRIMARY KEY,
    arm_id INT,
    timestamp TIMESTAMP,
    value FLOAT
);

CREATE TABLE RoboticArms (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    manufacturer VARCHAR(50),
    location VARCHAR(100)
);

CREATE TABLE ArmStatus (
    id INT PRIMARY KEY,
    arm_id INT,
    status VARCHAR(50),
    last_update TIMESTAMP
);

CREATE TABLE arm_movement (
  movement_id INT,
  arm_id INT,
  timestamp TIMESTAMP,
  movement_type VARCHAR(50)
);

CREATE TABLE arm_errors (
  error_id INT,
  arm_id INT,
  timestamp TIMESTAMP,
  error_message TEXT
);

CREATE TABLE arm_statistics (
  statistics_id INT,
  arm_id INT,
  timestamp TIMESTAMP,
  success_rate FLOAT
);
