tables.table_schema:
CREATE TABLE product_demand_reports (
    report_id INT PRIMARY KEY,
    product_id INT,
    report_date DATE,
    demand_quantity INT
);

CREATE TABLE sales_trends_reports (
    report_id INT PRIMARY KEY,
    product_id INT,
    report_date DATE,
    total_sales DECIMAL(10, 2)
);

CREATE TABLE product_demand (
    product_id INT PRIMARY KEY,
    demand_quantity INT,
    demand_date DATE
);

CREATE TABLE sales_trends (
    sale_id SERIAL PRIMARY KEY,
    product_id INT REFERENCES product_demand(product_id),
    sale_date DATE,
    sale_amount DECIMAL
);

CREATE TABLE products (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(255),
  category VARCHAR(50),
  price DECIMAL(10, 2)
);

CREATE TABLE customers (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(255),
  email VARCHAR(100),
  phone_number VARCHAR(20)
);

CREATE TABLE trends (
  trend_id INT PRIMARY KEY,
  description TEXT,
  start_date DATE,
  end_date DATE
);
