tables.table_schema:
CREATE TABLE regulations_compliance (
  id INT PRIMARY KEY,
  regulation_name VARCHAR(100),
  compliance_status VARCHAR(50)
);

CREATE TABLE industry_standards_compliance (
  id INT PRIMARY KEY,
  standard_name VARCHAR(100),
  compliance_rating INT
);

CREATE TABLE compliance_checklist (
  company_id INT,
  regulation_id INT,
  compliance_status VARCHAR(50)
);

CREATE TABLE regulations (
  regulation_id INT,
  regulation_name VARCHAR(100),
  regulation_description TEXT
);

CREATE TABLE industry_standards (
  standard_id INT,
  standard_name VARCHAR(100),
  standard_description TEXT
);

CREATE TABLE company (
  company_id INT,
  company_name VARCHAR(100),
  industry VARCHAR(50)
);
