tables.table_schema:
CREATE TABLE EmployeeTraining ( 
    employee_id INT PRIMARY KEY, 
    training_name VARCHAR(255), 
    training_date DATE 
);

CREATE TABLE CertificationRecords ( 
    certification_id INT PRIMARY KEY, 
    employee_id INT, 
    certification_name VARCHAR(255), 
    certification_date DATE 
);

CREATE TABLE employee_training (
  employee_id INT PRIMARY KEY,
  training_title TEXT,
  training_date DATE,
  training_duration INT
);

CREATE TABLE certification_records (
  certification_id INT PRIMARY KEY,
  employee_id INT,
  certification_name TEXT,
  certification_date DATE
);

CREATE TABLE skill_development (
  skill_id INT PRIMARY KEY,
  employee_id INT,
  skill_name TEXT,
  skill_level INT
);

CREATE TABLE employee_training (
	employee_id INT,
	employee_name VARCHAR(50),
	training_date DATE,
	training_topic VARCHAR(100)
);

CREATE TABLE employee_certifications (
	employee_id INT,
	certification_name VARCHAR(50),
	certification_date DATE,
	certification_organization VARCHAR(100)
);

CREATE TABLE training_topics (
	topic_id INT,
	topic_name VARCHAR(100),
	topic_description TEXT
);

CREATE TABLE certification_organizations (
	organization_id INT,
	organization_name VARCHAR(100),
	organization_description TEXT
);
