tables.table_schema:
CREATE TABLE quality_control_data (
    id INT PRIMARY KEY,
    product_id INT,
    defect_count INT,
    production_date DATE
);

CREATE TABLE defects ( 
    defect_id SERIAL PRIMARY KEY, 
    defect_type VARCHAR(50) NOT NULL, 
    defect_description TEXT, 
    timestamp TIMESTAMP WITH TIME ZONE DEFAULT current_timestamp
);

CREATE TABLE quality_control_data (
  id INT PRIMARY KEY,
  product_name VARCHAR(50),
  date TIMESTAMP,
  defect_rate DECIMAL
);

CREATE TABLE product_details (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(50),
  category VARCHAR(50),
  manufacturer VARCHAR(50)
);

CREATE TABLE statistical_analysis (
  analysis_id INT PRIMARY KEY,
  analysis_name VARCHAR(50),
  analysis_date TIMESTAMP,
  defect_count INT
);

CREATE TABLE defects (
   id INT PRIMARY KEY,
   defect_name VARCHAR(255),
   defect_type ENUM('minor', 'major', 'critical'),
   defect_severity INT
);

CREATE TABLE products (
   id INT PRIMARY KEY,
   product_name VARCHAR(255),
   manufacturing_date DATE,
   manufacturer VARCHAR(255)
);

CREATE TABLE defect_rates (
   id INT PRIMARY KEY,
   product_id INT REFERENCES products(id),
   defect_percentage DECIMAL(5,2),
   report_date DATE
);
