tables.table_schema:
CREATE TABLE Machines (
    machine_id INT PRIMARY KEY,
    machine_name VARCHAR(50),
    capacity INT,
    status ENUM('Active', 'Inactive')
);

CREATE TABLE ProductionTasks (
    task_id INT PRIMARY KEY,
    task_name VARCHAR(255),
    description TEXT,
    deadline TIMESTAMP
);

CREATE TABLE ProductionSchedule (
    schedule_id INT PRIMARY KEY,
    task_id INT,
    start_time TIMESTAMP,
    end_time TIMESTAMP
);

CREATE TABLE TaskDependencies (
    task_id INT,
    dependent_task_id INT,
    FOREIGN KEY (task_id) REFERENCES ProductionTasks(task_id),
    FOREIGN KEY (dependent_task_id) REFERENCES ProductionTasks(task_id)
);

CREATE TABLE TaskEfficiency (
    task_id INT,
    efficiency_percentage DECIMAL(5,2),
    performance_metric VARCHAR(50),
    PRIMARY KEY (task_id, performance_metric)
);
