tables.table_schema:
CREATE TABLE robot_equipment (
    robot_id INT PRIMARY KEY,
    robot_name VARCHAR(50),
    maintenance_schedule TEXT,
    service_history TEXT
);

CREATE TABLE RobotEquipment (
    equipment_id SERIAL PRIMARY KEY,
    equipment_name VARCHAR(255) NOT NULL,
    maintenance_schedule TEXT,
    service_history TEXT
);

CREATE TABLE MaintenanceTechnicians (
    technician_id SERIAL PRIMARY KEY,
    technician_name VARCHAR(255) NOT NULL,
    technician_specialty VARCHAR(255),
    technician_experience INT
);

CREATE TABLE MaintenanceSchedule (
  equipment_id UUID,
  maintenance_date DATE,
  maintenance_type VARCHAR,
  technician_name VARCHAR
);

CREATE TABLE EquipmentDetails (
  equipment_id UUID,
  equipment_name VARCHAR,
  manufacturer VARCHAR,
  purchase_date DATE
);

CREATE TABLE TechnicianDetails (
  technician_id UUID,
  technician_name VARCHAR,
  technician_specialization VARCHAR,
  technician_experience INT
);

CREATE TABLE service_engineers (
   engineer_id INT PRIMARY KEY,
   engineer_name VARCHAR(255),
   expertise_area VARCHAR(100)
);
