tables.table_schema:
CREATE TABLE machinery_maintenance (
    machinery_id SERIAL PRIMARY KEY,
    maintenance_date DATE NOT NULL,
    maintenance_type VARCHAR(50) NOT NULL,
    repair_needed BOOLEAN NOT NULL
);

CREATE TABLE machinery_maintenance (
   id INT PRIMARY KEY,
   machinery_name VARCHAR(255),
   maintenance_date DATE,
   maintenance_type VARCHAR(50)
);

CREATE TABLE technicians (
   id INT PRIMARY KEY,
   technician_name VARCHAR(255),
   technician_specialty VARCHAR(100),
   technician_experience INT
);

CREATE TABLE machinery (
    machinery_id INT PRIMARY KEY,
    machinery_name VARCHAR(255) NOT NULL,
    maintenance_schedule DATE NOT NULL,
    last_repair_date DATE
);

CREATE TABLE maintenance_logs (
    log_id INT PRIMARY KEY,
    machinery_id INT NOT NULL,
    log_date DATE NOT NULL,
    description TEXT
);

CREATE TABLE repair_records (
    record_id INT PRIMARY KEY,
    machinery_id INT NOT NULL,
    repair_date DATE NOT NULL,
    repair_cost DECIMAL(10,2)
);

CREATE TABLE maintenance_staff (
    staff_id INT PRIMARY KEY,
    staff_name VARCHAR(255) NOT NULL,
    specialization VARCHAR(255)
);

CREATE TABLE maintenance_schedule (
  machine_id INT,
  maintenance_date DATE,
  maintenance_type VARCHAR(50),
  technician_id INT
);

CREATE TABLE technician_details (
  technician_id INT,
  technician_name VARCHAR(100),
  specialization VARCHAR(100),
  experience_years INT
);

CREATE TABLE maintenance_logs (
  log_id INT,
  maintenance_id INT,
  log_date DATETIME,
  log_message TEXT
);
