tables.table_schema:
CREATE TABLE energy_consumption_data (
    facility_id INT PRIMARY KEY,
    consumption_date DATE,
    energy_usage_kwh DECIMAL(10,2),
    facility_location VARCHAR(50)
);

CREATE TABLE energy_consumption_data ( 
    facility_id INT NOT NULL, 
    timestamp TIMESTAMP NOT NULL, 
    energy_consumption FLOAT NOT NULL, 
    PRIMARY KEY (facility_id, timestamp) 
);

CREATE TABLE facilities ( 
    facility_id INT PRIMARY KEY, 
    facility_name TEXT NOT NULL, 
    location TEXT NOT NULL, 
    operational_cost FLOAT NOT NULL 
);

CREATE TABLE facility_data (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL,
    total_energy_consumption DECIMAL(10,2) NOT NULL
);

CREATE TABLE facilities (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE buildings (
    building_id INT PRIMARY KEY,
    facility_id INT NOT NULL,
    building_name VARCHAR(50) NOT NULL,
    building_size_sqft INT
);

CREATE TABLE energy_meters (
    meter_id INT PRIMARY KEY,
    building_id INT NOT NULL,
    meter_type VARCHAR(50) NOT NULL,
    installation_date DATE
);

CREATE TABLE energy_usage (
    usage_id INT PRIMARY KEY,
    meter_id INT NOT NULL,
    usage_date DATE NOT NULL,
    energy_consumed_kwh DECIMAL(10, 2) NOT NULL
);
