tables.table_schema:
CREATE TABLE ComplianceDocuments (
  doc_id INT PRIMARY KEY,
  doc_name VARCHAR(255),
  doc_type VARCHAR(50),
  compliance_status VARCHAR(20)
);

CREATE TABLE ComplianceLogs (
  log_id INT PRIMARY KEY,
  doc_id INT,
  action_taken VARCHAR(100),
  activity_date DATE
);

CREATE TABLE documentation (
    id INT PRIMARY KEY,
    document_title VARCHAR(255),
    document_description TEXT,
    date_created DATE
);

CREATE TABLE regulations (
    id INT PRIMARY KEY,
    regulation_name VARCHAR(255),
    regulation_description TEXT,
    compliance_deadline DATE
);

CREATE TABLE compliance_records (
    id INT PRIMARY KEY,
    document_id INT,
    regulation_id INT,
    compliance_status VARCHAR(50),
    date_updated TIMESTAMP
);

CREATE TABLE audit_logs (
    id INT PRIMARY KEY,
    user_id INT,
    action_performed VARCHAR(255),
    date TIMESTAMP
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(255),
    role VARCHAR(50)
);
