tables.table_schema:
CREATE TABLE manufacturing_processes (
  process_id INT PRIMARY KEY,
  product_name VARCHAR(255),
  inspector_id INT,
  inspection_result VARCHAR(50)
);

CREATE TABLE non_conforming_products (
  product_id INT PRIMARY KEY,
  issue_description TEXT,
  process_id INT,
  inspector_id INT
);

CREATE TABLE inspectors (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    inspector_id INT,
    FOREIGN KEY (inspector_id) REFERENCES inspectors(id)
);

CREATE TABLE defects (
    defect_id INT PRIMARY KEY,
    defect_type VARCHAR(255),
    product_id INT,
    FOREIGN KEY (product_id) REFERENCES products(product_id)
);

CREATE TABLE inspectors (
  id INT PRIMARY KEY,
  name VARCHAR(255)
);

CREATE TABLE products (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  inspector_id INT,
  FOREIGN KEY (inspector_id) REFERENCES inspectors(id)
);

CREATE TABLE manufacturing_process (
  id INT PRIMARY KEY,
  name VARCHAR(255)
);
