CREATE TABLE QualityMetrics (
    metric_id UUID PRIMARY KEY,
    metric_name STRING,
    metric_value FLOAT
);

CREATE TABLE Dashboards (
    dashboard_id UUID PRIMARY KEY,
    analyst_id UUID,
    dashboard_name STRING,
    visibility BOOLEAN
);

CREATE TABLE quality_metrics (
    metric_id SERIAL PRIMARY KEY,
    metric_name VARCHAR(50) NOT NULL,
    metric_value FLOAT NOT NULL
);

CREATE TABLE analysts (
    analyst_id SERIAL PRIMARY KEY,
    analyst_name VARCHAR(50) NOT NULL,
    analyst_department VARCHAR(100) NOT NULL
);

CREATE TABLE dashboards (
    dashboard_id SERIAL PRIMARY KEY,
    dashboard_name VARCHAR(50) NOT NULL,
    dashboard_description TEXT
);

CREATE TABLE analysts (
    analyst_id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL
);

CREATE TABLE metrics (
    metric_id INT PRIMARY KEY,
    metric_name VARCHAR(50) NOT NULL,
    metric_description VARCHAR(255)
);

CREATE TABLE dashboards (
    dashboard_id INT PRIMARY KEY,
    dashboard_name VARCHAR(50) NOT NULL,
    created_by INT,
    created_date DATE
);

CREATE TABLE dashboard_metrics (
    dashboard_id INT,
    metric_id INT,
    FOREIGN KEY (dashboard_id) REFERENCES dashboards(dashboard_id),
    FOREIGN KEY (metric_id) REFERENCES metrics(metric_id)
);

CREATE TABLE QualityMetrics (
    metric_id INT PRIMARY KEY,
    metric_name VARCHAR(255),
    metric_value FLOAT
);

CREATE TABLE Analysts (
    analyst_id INT PRIMARY KEY,
    analyst_name VARCHAR(255),
    analyst_role VARCHAR(255)
);

CREATE TABLE Dashboards (
    dashboard_id INT PRIMARY KEY,
    dashboard_name VARCHAR(255),
    dashboard_description TEXT
);
