tables.table_schema:
CREATE TABLE quality_control (
    id INT PRIMARY KEY,
    audit_id INT,
    inspector_name VARCHAR(255),
    audit_date DATE
);

CREATE TABLE audits (
    audit_id INT PRIMARY KEY,
    audit_name VARCHAR(255),
    audit_description TEXT,
    audit_type VARCHAR(100)
);

CREATE TABLE inspections (
    inspection_id INT PRIMARY KEY,
    audit_id INT,
    inspector_name VARCHAR(255),
    inspection_date DATE
);

CREATE TABLE quality_control_audit (
    id INT PRIMARY KEY,
    audit_date DATE,
    auditor_name VARCHAR(50),
    audit_description TEXT
);

CREATE TABLE inspection (
    id INT PRIMARY KEY,
    inspection_date DATE,
    inspector_name VARCHAR(50),
    inspection_description TEXT
);

CREATE TABLE quality_control_audit (
  audit_id UUID PRIMARY KEY,
  audit_date DATE,
  auditor_name VARCHAR(100),
  product_id UUID
);

CREATE TABLE product (
  product_id UUID PRIMARY KEY,
  product_name VARCHAR(100),
  manufacturer_id UUID,
  production_date DATE
);

CREATE TABLE manufacturer (
  manufacturer_id UUID PRIMARY KEY,
  manufacturer_name VARCHAR(100),
  location VARCHAR(100)
);

CREATE TABLE audit_result (
  result_id UUID PRIMARY KEY,
  audit_id UUID,
  audit_score INTEGER,
  feedback TEXT
);

CREATE TABLE inspection (
  inspection_id UUID PRIMARY KEY,
  inspection_date DATE,
  inspector_name VARCHAR(100),
  product_id UUID
);
