tables.table_schema:
CREATE TABLE production_line_performance_data (
  production_line_id INT PRIMARY KEY,
  time_stamp TIMESTAMP,
  production_rate INT,
  efficiency_rate DECIMAL
);

CREATE TABLE production_line_performance (
  production_line_id INT PRIMARY KEY,
  efficiency DECIMAL,
  downtime_minutes INT,
  running_status BOOLEAN
);

CREATE TABLE production_line_performance (
    id INT PRIMARY KEY,
    product_id INT,
    production_time TIMESTAMP,
    efficiency DECIMAL(10, 2)
);

CREATE TABLE product_details (
    product_id INT PRIMARY KEY,
    product_name TEXT,
    product_type TEXT,
    creation_date DATE
);

CREATE TABLE real_time_data (
    data_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    sensor_id INT,
    value DECIMAL(10, 2)
);

CREATE TABLE production_errors (
  id SERIAL PRIMARY KEY,
 line_id INT,
 error_timestamp TIMESTAMP,
 error_message TEXT
);

CREATE TABLE production_line_info (
  id SERIAL PRIMARY KEY,
 line_id INT,
 production_capacity INT,
 current_speed INT
);

CREATE TABLE production_logs (
  id SERIAL PRIMARY KEY,
 log_timestamp TIMESTAMP,
 action VARCHAR(100),
 description TEXT
);
