tables.table_schema:
CREATE TABLE Equipment (
    id INT PRIMARY KEY,
    name VARCHAR(100),
    location VARCHAR(100)
);

CREATE TABLE Sensor (
    id INT PRIMARY KEY,
    type VARCHAR(50),
    status VARCHAR(20),
    equipment_id INT
);

CREATE TABLE SensorData (
    id INT PRIMARY KEY,
    sensor_id INT,
    timestamp TIMESTAMP,
    value FLOAT
);

CREATE TABLE Equipment ( 
    equipment_id INT PRIMARY KEY, 
    equipment_name VARCHAR(255) NOT NULL, 
    location VARCHAR(255) NOT NULL 
);
