tables.table_schema:
CREATE TABLE ProductionSchedule (
    id SERIAL PRIMARY KEY,
    product_name VARCHAR(50),
    start_time TIMESTAMP,
    end_time TIMESTAMP
);

CREATE TABLE ResourceAllocation (
    id SERIAL PRIMARY KEY,
    resource_name VARCHAR(50),
    resource_type VARCHAR(50),
    allocated_quantity INT
);

CREATE TABLE OptimizationAlgorithms (
    id SERIAL PRIMARY KEY,
    algorithm_name VARCHAR(50),
    optimization_type VARCHAR(50),
    algorithm_description TEXT
);

CREATE TABLE resource_allocation (
    resource_id INT PRIMARY KEY,
    resource_name VARCHAR(50),
    resource_type VARCHAR(50),
    quantity INT
);

CREATE TABLE schedule_optimization (
    optimization_id INT PRIMARY KEY,
    optimization_name VARCHAR(50),
    optimization_type VARCHAR(50),
    optimization_date TIMESTAMP
);

CREATE TABLE production_capacity (
    capacity_id INT PRIMARY KEY,
    capacity_name VARCHAR(50),
    capacity_type VARCHAR(50),
    production_limit INT
);

CREATE TABLE allocation_history (
    history_id INT PRIMARY KEY,
    allocation_date TIMESTAMP,
    resource_id INT,
    quantity_allocated INT
);
