tables.table_schema:
CREATE TABLE defect_rates (
    id SERIAL PRIMARY KEY,
    defect_type VARCHAR(255),
    defect_severity INT,
    production_date DATE
);

CREATE TABLE production_line (
    id SERIAL PRIMARY KEY,
    product_name VARCHAR(255),
    production_speed INT,
    defect_rate FLOAT
);

CREATE TABLE defect_reports (
  report_id INT PRIMARY KEY,
  defect_type TEXT,
  production_line_id INT,
  defect_date DATE
);

CREATE TABLE production_lines (
  line_id INT PRIMARY KEY,
  line_name TEXT,
  supervisor_id INT
);

CREATE TABLE waste_reduction (
  reduction_id INT PRIMARY KEY,
  reduction_method TEXT,
  implementation_date DATE,
  outcome TEXT
);

CREATE TABLE defect_rate (
   id SERIAL PRIMARY KEY,
   product_name TEXT NOT NULL,
   defect_type TEXT NOT NULL,
   defect_count INT NOT NULL
);

CREATE TABLE production_line (
   id SERIAL PRIMARY KEY,
   line_number INT NOT NULL,
   line_manager TEXT NOT NULL,
   start_date DATE NOT NULL
);

CREATE TABLE defect_location (
   id SERIAL PRIMARY KEY,
   defect_id INT NOT NULL,
   location TEXT NOT NULL,
   area TEXT NOT NULL
);
