tables.table_schema:
CREATE TABLE production_stage( 
    stage_id INT PRIMARY KEY, 
    stage_name TEXT NOT NULL 
);

CREATE TABLE quality_check( 
    check_id INT PRIMARY KEY, 
    check_name TEXT NOT NULL, 
    performed_by TEXT NOT NULL 
);

CREATE TABLE check_result( 
    result_id INT PRIMARY KEY, 
    check_id INT REFERENCES quality_check(check_id), 
    stage_id INT REFERENCES production_stage(stage_id), 
    result TEXT NOT NULL 
);

CREATE TABLE stage_1_checks (
    id INT PRIMARY KEY,
    check_result VARCHAR(50),
    check_date DATE
);

CREATE TABLE stage_2_checks (
    id INT PRIMARY KEY,
    check_result VARCHAR(50),
    check_date DATE
);

CREATE TABLE stage_3_checks (
    id INT PRIMARY KEY,
    check_result VARCHAR(50),
    check_date DATE
);

CREATE TABLE compliance_reports (
    report_id INT PRIMARY KEY,
    stage_1_passed BOOLEAN,
    stage_2_passed BOOLEAN,
    stage_3_passed BOOLEAN
);
