tables.table_schema:
CREATE TABLE suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    location VARCHAR(255),
    sustainability_rating INT
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    price DECIMAL,
    supplier_id INT,
    FOREIGN KEY (supplier_id) REFERENCES suppliers(supplier_id)
);

CREATE TABLE sustainability_metrics (
    metric_id INT PRIMARY KEY,
    metric_name VARCHAR(255),
    supplier_id INT,
    sustainability_score FLOAT,
    FOREIGN KEY (supplier_id) REFERENCES suppliers(supplier_id)
);

CREATE TABLE supplier_information( 
 supplier_id INT PRIMARY KEY, 
 supplier_name VARCHAR(50), 
 location VARCHAR(100), 
 contact_email VARCHAR(50) 
 );

CREATE TABLE sustainability_metrics( 
 metric_id INT PRIMARY KEY, 
 metric_name VARCHAR(50), 
 value FLOAT, 
 timestamp TIMESTAMP 
 );

CREATE TABLE supplier_relations( 
 relation_id INT PRIMARY KEY, 
 supplier_id INT, 
 related_supplier_id INT 
 );

CREATE TABLE sustainability_rankings( 
 ranking_id INT PRIMARY KEY, 
 supplier_id INT, 
 sustainability_score FLOAT 
 );

CREATE TABLE Suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    location VARCHAR(255),
    contact_email VARCHAR(255)
);

CREATE TABLE GreenCertifications (
    certification_id INT PRIMARY KEY,
    certification_name VARCHAR(255),
    expiry_date DATE,
    supplier_id INT
);

CREATE TABLE SupplierContacts (
    contact_id INT PRIMARY KEY,
    contact_name VARCHAR(255),
    contact_title VARCHAR(100),
    supplier_id INT
);

CREATE TABLE SupplierCategories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255),
    category_description TEXT,
    supplier_id INT
);
