tables.table_schema:
CREATE TABLE manufacturing_equipment (
    equipment_id INT PRIMARY KEY,
    equipment_name VARCHAR(255),
    energy_consumption DECIMAL
);

CREATE TABLE manufacturing_locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(255),
    equipment_id INT,
    FOREIGN KEY (equipment_id) REFERENCES manufacturing_equipment(equipment_id)
);

CREATE TABLE Manufacturing_Equipment (
  equipment_id INT PRIMARY KEY,
  equipment_name VARCHAR(50),
  manufacturing_date DATE,
  energy_consumption DECIMAL
);

CREATE TABLE manufacturing_equipment (
  equipment_id UUID PRIMARY KEY,
  equipment_name VARCHAR(50) NOT NULL,
  manufacturer VARCHAR(50) NOT NULL,
  production_date DATE
);

CREATE TABLE energy_consumption (
  entry_id SERIAL PRIMARY KEY,
  equipment_id UUID REFERENCES manufacturing_equipment (equipment_id),
  consumption_value DECIMAL(8, 2) NOT NULL,
  consumption_date TIMESTAMP NOT NULL
);

CREATE TABLE equipment_status (
  status_id SERIAL PRIMARY KEY,
  equipment_id UUID REFERENCES manufacturing_equipment (equipment_id),
  status_code VARCHAR(10) NOT NULL,
  status_date TIMESTAMP
);

CREATE TABLE manufacturing_equipment (
    equipment_id INT PRIMARY KEY,
    equipment_name VARCHAR(255),
    location VARCHAR(255),
    energy_consumption DECIMAL(10, 2)
);

CREATE TABLE maintenance_logs (
    log_id INT PRIMARY KEY,
    equipment_id INT,
    log_date DATE,
    description TEXT
);

CREATE TABLE energy_costs (
    cost_id INT PRIMARY KEY,
    equipment_id INT,
    cost DECIMAL(10, 2),
    cost_date DATE
);

CREATE TABLE energy_metrics (
    metric_id INT PRIMARY KEY,
    equipment_id INT,
    metric_name VARCHAR(255),
    metric_value DECIMAL(10, 2)
);

CREATE TABLE energy_trends (
    trend_id INT PRIMARY KEY,
    equipment_id INT,
    trend_date DATE,
    trend_value DECIMAL(10, 2)
);
