tables.table_schema:
CREATE TABLE Water_Usage_Trends (
  Facility_ID INT,
  Date DATE,
  Water_Usage_Gallons FLOAT,
  Flow_Rate_GPM FLOAT
);

CREATE TABLE manufacturing_facility_water_usage (
    facility_id INT PRIMARY KEY,
    consumption_amount DECIMAL,
    timestamp TIMESTAMP
);

CREATE TABLE water_usage_trends (
    trend_id INT PRIMARY KEY,
    facility_id INT,
    trend_description TEXT
);

CREATE TABLE WaterConsumption (
    ID INT PRIMARY KEY,
    Location VARCHAR(255),
    Consumption DECIMAL,
    Date DATE
);

CREATE TABLE FacilityEquipment (
    EquipmentID INT PRIMARY KEY,
    EquipmentName VARCHAR(255),
    Location VARCHAR(255),
    LastMaintenance DATE
);

CREATE TABLE MonthlyUsageTrends (
    TrendID INT PRIMARY KEY,
    MonthYear VARCHAR(10),
    AverageUsage DECIMAL,
    PeakUsage DECIMAL
);

CREATE TABLE Facility (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE WaterMeter (
    meter_id INT PRIMARY KEY,
    facility_id INT NOT NULL,
    meter_reading FLOAT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE Alert (
    alert_id INT PRIMARY KEY,
    meter_id INT NOT NULL,
    alert_type VARCHAR(50) NOT NULL,
    alert_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE UsageTrend (
    trend_id INT PRIMARY KEY,
    meter_id INT NOT NULL,
    trend_value FLOAT NOT NULL,
    trend_timestamp TIMESTAMP NOT NULL
);
